"use strict";

document.addEventListener("DOMContentLoaded", function () {
  getFilms();

  function getFilms() {
    var searchStr = arguments.length > 0 && arguments[0] !== undefined ? arguments[0] : '';
    var apiUrl = 'https://swapi.co/api/films/';
    fetch(apiUrl).then(function (response) {
      return response.json();
    }).then(function (data) {
      var _loop = function _loop(i) {
        if (searchStr !== '' && searchStr !== undefined) {
          if (data.results[i].title.toLowerCase().indexOf(searchStr.toLowerCase()) < 0) {
            return "continue";
          }
        }

        $('.films_list').append($('<div/>', {
          "class": 'film',
          id: 'film-' + i
        }).append($('<div/>', {
          "class": 'film_img'
        }).append($('<img/>', {
          src: '',
          alt: data.results[i].title
        }))).append($('<div/>', {
          "class": 'film_desc'
        }).append($('<h2/>', {
          "class": 'film_title',
          text: data.results[i].title
        })).append($('<p/>', {
          "class": 'film_producer',
          text: data.results[i].producer
        }))));
        var apiPhotoUrl = 'https://pixabay.com/api/?key=13469464-412b7bcd981b5a6c95e7f09b9&q=' + data.results[i].title + '&image_type=photo';
        fetch(apiPhotoUrl).then(function (response) {
          return response.json();
        }).then(function (data) {
          var filmImgSrc;

          if (data.hits.length > 0) {
            filmImgSrc = data.hits[0].webformatURL;
          } else {
            filmImgSrc = 'http://placehold.it/260x85?text=Placeholder';
          }

          $('#film-' + i).find('img').attr('src', filmImgSrc);
        });
      };

      for (var i = 0; i < data.results.length; i++) {
        var _ret = _loop(i);

        if (_ret === "continue") continue;
      }
    });
  }

  var searchBtn = document.querySelector('.search_btn');

  if (searchBtn) {
    searchBtn.onclick = function (e) {
      e.preventDefault();
      var searchStr = document.querySelector('#film_input').value;
      $('.films_list .film').fadeOut(200);
      setTimeout(function () {
        return $('.films_list').empty();
      }, 200);
      getFilms(searchStr);
    };
  }

  $('#film_input').keypress(function (e) {
    if (e.which == 13) {
      e.preventDefault();
      return false;
    }
  });
});